import React from 'react';
import './app.css';
const appId = process.env;

export class App extends React.Component {
  state = {
    data: undefined,
  };
  async componentDidMount() {
    const requestUrl = `https://api.openweathermap.org/data/2.5/weather?appid=${appId.REACT_APP_APP_ID}&q=London&units=metric`;
    const response = await fetch(requestUrl);
    const data = await response.json();
    this.setState({ data });
    console.log(this.state.data);
  }
  render() {
    if (!this.state.data || !this.state.data.main) {
      return <h2>LOADING...</h2>;
    }
    return (
      <div className='main'>
        <div className='temp'>
          <div className='temp__feel'></div>
          <div className='temp__main'></div>
          <div className='temp__min'></div>
          <div className='temp__max'></div>
        </div>
        <div className='weather'></div>
        <div className='location'>
          <h2>Город:</h2>
          <h2>{this.state.data.name}</h2>
        </div>
      </div>
    );
  }
}
