import React from 'react';
import './app.css';
import { MainWeather } from '../weather';
const { REACT_APP_APP_ID } = process.env;
import { LocationForm } from '../locationForm';
export class App extends React.Component {
  state = {
    data: {},
    location: 'London',
  };
  async componentDidMount() {
    const requestUrl = `https://api.openweathermap.org/data/2.5/weather?appid=${REACT_APP_APP_ID}&q=${this.state.location}&units=metric`;
    const response = await fetch(requestUrl);
    const data = await response.json();
    this.setState({ data });
    console.log(this.state.location);
  }
  render() {
    if (!this.state.data || !this.state.data.main) {
      return <h2>LOADING...</h2>;
    }

    return (
      <div>
        <MainWeather data={this.state.data} location={this.state.location} />
        <LocationForm />
      </div>
    );
  }
}
